var btn=document.querySelector(".btn-search-appartments"),searchForm=document.querySelector(".search-form"),dateCheckIn=searchForm.querySelector("[name=check-in-date]"),dateCheckOut=searchForm.querySelector("[name=check-out-date]"),adults=searchForm.querySelector("[name=adults]"),children=searchForm.querySelector("[name=children]");btn.addEventListener("click",function(e){e.preventDefault(),searchForm.classList.toggle("hide-search"),dateCheckIn.focus()}),searchForm.addEventListener("submit",function(e){dateCheckIn.value&&dateCheckOut.value&&adults.value?(localStorage.setItem("adults",adults.value),localStorage.setItem("children",children.value)):(e.preventDefault(),console.log("Нужно указать дату заезда и дату выезда, а также количество гостей"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&searchForm.classList.add("hide-search")});